'use strict';

var Logger = require('./index.js');

var logger = new Logger({
    logLevel: 5,
});

logger.log('log');
logger.warn('warn');
logger.error('error');
logger.debug('debug');
logger.trace('trace');

function A() {
}

var a = new A();

logger.mixin(a);

a.log('log');
a.warn('warn');
a.error('error');
a.debug('debug');
a.trace('trace');
